SELECT k.id, k.spu_id AS spuId, k.bar_code AS barCode, p.freight_template_id AS freightTemplateId, p.category_id AS categoryId, p.activity_type AS activityType, p.activity_id AS activityId, p.gmt_activity_start AS gmtActivityStart, p.gmt_activity_end AS gmtActivityEnd, k.specification, k.title, p.title AS spuTitle, k.img, p.img AS spuImg, k.original_price AS originalPrice, k.price, k.vip_price AS vipPrice, k.stock, k.weight, p.unit AS unit, k.gmt_update AS gmtUpdate, k.gmt_create AS gmtCreate FROM unimall_sku AS k, unimall_spu AS p WHERE k.id IN
#{id}
AND k.spu_id = p.id
